create table t_mottu_cargo (id_cargo bigint generated by default as identity, nome_cargo enum ('ADMIN','AUXILIAR','CEO','COORDENADOR','ESTAGIARIO','GERENTE','MECANICO','SUPERVISOR'), primary key (id_cargo));
create table t_mottu_filiais (id_filial bigint generated by default as identity, end_filial varchar(255) not null, primary key (id_filial));
create table t_mottu_localizacoes (pos_x numeric(4,3) not null, pos_y numeric(4,3) not null, status_localizacao integer not null, data_localizacao timestamp(6) not null, id_localizacao bigint generated by default as identity, id_moto bigint, id_imagem varchar(255) not null, primary key (id_localizacao));
create table t_mottu_motos (status_moto integer not null, id_filial bigint not null, id_imei bigint, id_moto bigint generated by default as identity, num_motor bigint, id_qr_code varchar(255), modelo_moto varchar(255), num_chassi varchar(255), placa_moto varchar(255), primary key (id_moto));
create table t_mottu_usuario (idade integer not null, id_filial bigint not null, id_usuario bigint generated by default as identity, senha varchar(60) not null, primeiro_nome varchar(50) not null, sobrenome varchar(50) not null, email varchar(100) not null, primary key (id_usuario));
create table t_usuario_cargo (id_cargo bigint not null, id_usuario bigint not null, primary key (id_cargo, id_usuario));